const projectList = document.getElementById('project-list');const projectDetails = document.getElementById('project-details');fetch('assets/dummy-data.json').then(response => response.json()).then(data => {data.forEach(project => {const projectCard = document.createElement('div');projectCard.innerHTML = `<h2>${project.title}</h2><p>${project.description}</p><button data-project-id="${project.id}">View Details</button>`;projectList.appendChild(projectCard);projectCard.querySelector('button').addEventListener('click', () => {showProjectDetails(project);});});});function showProjectDetails(project) {projectDetails.innerHTML = `<h2>${project.title}</h2><p>${project.description}</p><p>Budget: $${project.budget}</p><p>Timeline: ${project.timeline}</p>`;projectList.style.display = 'none';projectDetails.style.display = 'block';}